import React, { useState } from "react";
import About from "./components/About.js";
import Contact from "./components/Contact.js";
import Portfolio from "./components/Portfolio.js";
import Resume from "./components/Resume.js";
import Services from "./components/Services.js";
//import Nav from "./components/Nav.js";

const Nav = () =>{
  <div className="side-nav">
    {links.map(link => (
      <a 
        key={link.id} 
        href="#" 
        onClick={() => switchContent(link.id)}
        className={activeLink === link.id ? 'active' : ''}
      >
        {link.text}
      </a>
    ))}
  </div>
}

const App = () => {
  
  const [activeLink, setActiveLink] = useState(1);

  const links = [
    { id: 1, text: 'About', content: <About /> },
    { id: 2, text: 'Contact', content: <Contact /> },
    { id: 3, text: 'Portfolio', content: <Portfolio /> },
    { id: 4, text: 'Resume', content: <Resume /> },
    { id: 5, text: 'Services', content: <Services /> }
  ];

  const switchContent = id => {
    setActiveLink(id);
  };

  return (
    <div className="App">
      <Nav />
      <div className="content">
        {links.find(link => link.id === activeLink).content}
      </div>
    </div>
  );
}

export default App;
